import React, { useState } from "react";
import { getDownloadURL, getStorage, ref, uploadBytes } from "firebase/storage";
import { db } from "../../../Firebase";
import { addDoc, collection } from "firebase/firestore";
import { useForm } from "react-hook-form";

function AddBank() {
  const {
    register,
    handleSubmit,
    formState: { errors },
    reset,
    trigger,
  } = useForm();

  const CollectionRef = collection(db, "Bank");
  const [ImgUpload, setImgUpload] = useState("");
  const storage = getStorage();
  const [Url, setUrl] = useState("");

  const HandleUpload = async (folder, refname, file) => {
    const storageRef = ref(storage, `${folder}/${refname}`);
    await uploadBytes(storageRef, file).then((snapshot) => {
      console.log("Uploaded a blob or file!");
      getDownloadURL(snapshot.ref).then((downloadURL) => {
        setUrl(downloadURL);

        console.log("File available at", downloadURL);
      });
    });
  };

  const addDataEvent = async (data) => {
    await addDoc(CollectionRef, {
      photo: Url,
      accountNO: data.accountNO,
      name: data.name,
      ifsc: data.ifsc,
      upiID: data.upiID,
    }).then(() => {
      return true;
    });
  };

  const subForEvent = async (data) => {
    // e.preventDefault();
    setImgUpload(data.photo[0]);
    console.log(data);
    console.log(ImgUpload);

    if (ImgUpload !== "") {
      await HandleUpload("Bank", ImgUpload.name, ImgUpload)
        .then(() => {
          alert("image uploaded  :)");
          console.log("url ::::::   ", Url);

          if (Url !== "") {
            let ok = addDataEvent(data);
            if (ok) {
              alert("Succussfully registered");
              setUrl("");
              reset();
            }
          } else {
            alert("try again !");
          }
        })
        .catch((error) => console.log(error.message));
    } else {
      alert("image not uploaded !");
    }
  };
  return (
    <div>
      <div
        style={{
          padding: "2rem",
          backgroundColor: "rgba(0,0,0,0.4)",
        }}
      >
        <div className="bx">
          <form onSubmit={handleSubmit(subForEvent)}>
            <label style={{ textAlign: "center" }}>
              QR Code
              <input
                style={{
                  height: "150px",
                  width: "150px",
                  marginLeft: "33rem",
                  backgroundImage:
                    "url(data:image/png;base64,)",
                  backgroundRepeat: "round",
                }}
                type="file"
                className="form-control"
                name="photo"
                {...register("photo", {
                  required: "photo is required",
                })}
                onKeyUp={() => trigger("photo")}
              />
              {errors.photo && (
                <div>
                  <small className="text-danger">{errors.photo.message}</small>
                </div>
              )}
            </label>

            <label>
              Account Holder's Name
              <input
                className="form-control"
                name="name"
                {...register("name", {
                  required: "name is required",
                })}
                onKeyUp={() => trigger("name")}
              />
              {errors.name && (
                <div>
                  <small className="text-danger">{errors.name.message}</small>
                </div>
              )}
            </label>

            <label>
              Account Number
              <input
                className="form-control"
                name="accountNO"
                {...register("accountNO", {
                  required: "accountNO is required",
                })}
                onKeyUp={() => trigger("accountNO")}
              />
              {errors.accountNO && (
                <div>
                  <small className="text-danger">
                    {errors.accountNO.message}
                  </small>
                </div>
              )}
            </label>

            <label>
              IFSC
              <input
                className="form-control"
                name="ifsc"
                {...register("ifsc", {
                  required: "ifsc is required",
                })}
                onKeyUp={() => trigger("ifsc")}
              />
              {errors.ifsc && (
                <div>
                  <small className="text-danger">{errors.ifsc.message}</small>
                </div>
              )}
            </label>

            <label>
              UPI ID
              <input
                className="form-control"
                name="upiID"
                {...register("upiID", {
                  required: "upiID is required",
                })}
                onKeyUp={() => trigger("upiID")}
              />
              {errors.upiID && (
                <div>
                  <small className="text-danger">{errors.upiID.message}</small>
                </div>
              )}
            </label>

            <div>
              <input
                style={{ width: "100%" }}
                className="btn btn-success"
                type="submit"
                value="add bank"
              />
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default AddBank;
